/* eslint-disable react/prop-types */
import { CloseOutlined } from '@ant-design/icons'
import { Image } from 'antd'
import { useEffect, useState } from 'react'
import usePlacePhoto from '../../../hooks/usePlacePhoto'
import searchStore from '../../../store/searchStore'
import styles from './PlaceCard.module.css'

function PlaceCard() {
  const [toggle, setToggle] = useState(false)
  const [searchData, setSearchData] = useState<any>()
  const { photo } = usePlacePhoto()
  const [photos, setPhotos] = useState({})

  useEffect(() => {
    searchStore.subscribe(setSearchData)
  }, [])

  const onClick = () => {
    setToggle((toggle) => !toggle)
    console.log('toggle', toggle)
  }

  useEffect(() => {
    setToggle(searchData ? true : false)

    if (toggle) {
      searchData?.photos?.forEach((data, idx) => {
        photo(data.photo_reference, (response) => {
          setPhotos((p) => {
            return { ...p, [idx]: response }
          })
        })
      })
    }
  }, [searchData])

  const classes = `${styles.cardContainer}  ${
    searchData ? (toggle ? styles.show : styles.hide) : styles.initHide
  }`

  console.log(searchData)
  return (
    <div className={classes}>
      <div className={styles.top}>
        <span className={styles.title}>{searchData?.value}</span>
        <CloseOutlined onClick={onClick} className={styles.closeIcon} />
      </div>
      <div className={styles.photos}>
        {Object.values(photos).map((value: any) => {
          return (
            <Image
              key={value.split('/').pop()}
              src={value}
              fallback='data:image/png;base64,'
            />
          )
        })}
      </div>
    </div>
  )
}
export default PlaceCard
